<div class="row">
    <div class="input-field col s6 m4">
        <select >
            <option value="aluga">Aluga</option>
            <option value="venda">Venda</option>
        </select>
        <label>Status</label>
    </div>
    <div class="input-field col s6 m4">
        <select >
            <option value="1">Casa</option>
            <option value="2">Apartamento</option>
            <option value="3">Kitnet</option>
        </select>
        <label>Tipo</label>
    </div>
    <div class="input-field col s6 m4">
        <select >
            <option value="aluga">Barretos</option>
            <option value="venda">São José do Rio Preto</option>
        </select>
        <label>Cidade</label>
    </div>
    <div class="input-field col s6 m3">
        <select>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
        </select>
        <label>Dormitórios</label>
    </div>
    <div class="input-field col s12 m4">
        <select >
            <option value="1">Até R$ 500,00</option>
            <option value="2">De R$ 500,00 a R$ 1.000,00</option>
            <option value="3">De R$ 1.001,00 a R$ 5.000,00</option>
            <option value="4">De R$ 5001,00 a R$ 10.000,00</option>
            <option value="5">De R$ 10.001,00 a R$ 20.000,00</option>
            <option value="6">De R$ 20.001,00 a R$ 30.000,00</option>
        </select>
        <label for="">Valor</label>
    </div>
    <div class="input-field col s12 m3">
        <input type="text" class="validate">
        <label>Bairro</label>
    </div>
    <div class="input-field col s12 m2">
        <button class="btn deep-orange darken-1">Filtar</button>
    </div>
</div>
